<template>
  <section class="max-w-2xl mx-auto p-4 bg-white shadow-lg border-r">
    <h1 class="text-3xl font-indie text-center text-gray-800 mb-6">Art Submission</h1>

    <SubmitForm @updatePreview="updatePreview" class="mb-8" />

    <section class="mb-8">
      <h2 class="font-indie text-2xl text-gray-700">Preview</h2>
      <ArtPiece :artPieceData="artPieceData" />
    </section>

    <button
      class="w-full font-indie text-lg cursor-pointer border border-green-500 p-3 rounded-lg bg-green-100 text-green-700 hover:bg-green-200 active:bg-green-300 transition-all duration-200 ease-in-out transform hover:scale-102 active:scale-98"
      @click="submitArt"
    >
      Ship My Art!
    </button>
  </section>
</template>

<script setup lang="ts">
import SubmitForm from './SubmitForm.vue'
import ArtPiece from './ArtPiece.vue'
import { reactive } from 'vue'

const emit = defineEmits(['submitArt'])

let confirm = false

const artPieceData = reactive({
  imageSource:
    'data:image/png;base64,',
  description: 'Placeholder image and description!',
})

function updatePreview(newArtPieceData: { imageSource: string; description: string }) {
  artPieceData.imageSource =
    newArtPieceData.imageSource ||
    'data:image/png;base64,'
  artPieceData.description = newArtPieceData.description || 'Placeholder image and description!'
}

function submitArt() {
  emit('submitArt', artPieceData)
}
</script>
